import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './modal.css';

class Modal extends Component {
    render() {
        return (
            <div className={"modal-container modal-container-collapsed"}>
                <div className={"modal"}>
                    <Info/>
                    <Navigation/>
                </div>
            </div>
        )
    }
}


class Info extends Component{
    render() {
        // ABOUT
        const about = {
            h: 'About',
            t: 'Abstrakt Studio \n Creative Kollektiv by yngzy, therawpro & zizou.'
        }
        const about_heading = React.createElement('h3', {className: 'm-h3'},
            about.h)
        const about_text = React.createElement('p', {className: 'm-p'},
            about.t)



        // SKILLS
        const skills = {
            h: 'Skills',
            t: ['Art Direction', 'Animations', 'Graphic Design', '3D Visuals', '3D Scans', 'Webdesign', 'Webdevelopment']
        }
        const skills_heading = React.createElement('h3', {className: 'm-h3'},
            skills.h)
        const skills_text = React.createElement('p', {className: 'm-p'},
            skills.t)

        // CLIENTS & PROJECTS
        const clients = {
            h: 'Clients & Projects',
            t: '12345'
        }
        const clients_heading = React.createElement('h3', {className: 'm-h3'},
            clients.h)
        const clients_text = React.createElement('p', {className: 'm-p'},
            clients.t)

        const xxx = {
            1: 'Webdesign and Development by Lukas Kutsch',
            2: 'Inter Typeface (https://rsms.me/inter/)'
        }

        return (
            <div className={"info-container"}>
                <div className={'info-box'} id={'about-box'}>
                    {about_heading}
                    {about_text}
                </div>
                <div className={'info-box'} id={'studio-box'}>
                    {studio_heading}
                    {studio_text}
                </div>
                <div className={'info-box'} id={'skills-box'}>
                    {skills_heading}
                    {skills_text}
                </div>
                <div className={'info-box'} id={'clients-box'}>
                    {clients_heading}
                    {clients_text}
                </div>
            </div>
        )
    }
}

class Navigation extends Component {
    render() {
        return (
            <div className={"nav-container"}>

            </div>
        )
    }
}

class InfoStudio extends Component {
    render() {
        const studio = {
            h: 'Studio',
            t1: 'Mustermannstraße 1',
            t2: '12345 Stadt',
            t3: 'Germany',
            t4: '0157 00000 0000',
            t5: 'abstrakt.studio@gmail.com'
        }
        const studio_text = <div className={'m-p'}> {studio.t1} </div>
        return (
            <div className={'infobox'}>
                <h3 className={'m-h3'}>
                    {studio.h}
                </h3>
                <p className={'m-p'}>
                    {studio.t1}
                </p>
                <p className={'m-p'}>
                    {studio.t2}
                </p>
                <p className={'m-p'}>
                    {studio.t3}
                </p>
                <p className={'m-p'}>
                    {studio.t4}
                </p>
                <p className={'m-p'}>
                    {studio.t5}
                </p>
            </div>

        )
    }
}

export default Modal
