import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './header.css';
import './menu.css';

class Header extends Component {
    render() {
        return(
            <div className={"header-container"}>
                <Logo/>
                <MenuToggle/>
            </div>
        )
    }
}

class Logo extends Component {
    render() {
        function openMenu() {
            let element = document.querySelector(".menu-container").style.display
            if (element === "block") {
                element = "none";
            }
            else {
                element = "block";
            }
        }

        return (
            <div className={"logo-container"}>
                <a className={"a-logo"} onClick={openMenu}>
                    <svg className={"logo"} id="Layer_1" xmlns="http://www.w3.org/2000/svg" width="33" height="41" viewBox="0 0 33 41">
                        <path className="st0" d="M1 40l30-5.4L1 9.5z"></path>
                        <path className="st0 st0--second" d="M4.6 5.7L31 1 4.5 32.9z"></path>
                    </svg>
                </a>
            </div>
        )
    }
}

class MenuToggle extends Component {
    render() {
        return (
            <div className={"menu-toggle-container"}>
                <a className={"a-menu-toggle"}>
                    <div className={"menu-toggle-line"}/>
                    <div className={"menu-toggle-line"}/>
                    <div className={"menu-toggle-line"}/>
                </a>
            </div>
        )
    }
}

export default Header
