import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './header.css';
import './menu.css';

class Header extends Component {
    render() {
        return(
            <div className={"header-container"}>
                <Logo/>
                <MenuToggle/>
            </div>
        )
    }
}

class Logo extends Component {
    render() {
        return (
            <div className={"logo-container"}>
                <a className={"a-logo"}>
                    <svg className={"logo"} xmlns="http://www.w3.org/2000/svg" width="33" height="41" viewBox="0 0 33 41">
                        <path d="M1 40l30-5.4L1 9.5z"/>
                        <path d="M4.6 5.7L31 1 4.5 32.9z"/>
                    </svg>
                </a>
            </div>
        )
    }
}

class MenuToggle extends Component {
    render() {
        function openMenu() {
            let element = document.querySelector(".menu-container").classList
            if (element.contains("menu-container-collapsed")) {
                element.remove("menu-container-collapsed")
                element.add("menu-container-expanded")
            }
            else {
                element.remove("menu-container-expanded")
                element.add("menu-container-collapsed")
            }
        }


        return (
            <div className={"menu-toggle-container"}>
                <a className={"a-menu-toggle"} onClick={openMenu}>
                    <div className={"menu-toggle-line"}/>
                    <div className={"menu-toggle-line"}/>
                    <div className={"menu-toggle-line"}/>
                </a>
            </div>
        )
    }
}

export default Header
